import React, { useContext, useEffect, useState } from "react";
import Navbar from "../Components/Navbar";
import Footer from "../Components/Footer";
import { apiValue } from "../Data/AllData";
import axios from "axios";

function AllShop() {

	const product = useContext(apiValue);

	// const navigate = useNavigate();
	// 	const handleClick = () => {
	// 		navigate("/shop");
	// 	}
	const [cats, setCats] = useState([]);
	const [selectedCat, setSelectedCat] = useState("");
	const [filterItems, setFilterItems] = useState(product);

	useEffect(() => {
		axios
			.get("https://dummyjson.com/products/category-list")
			.then((element) => {
				setCats(element.data);
			});
	}, []);

	useEffect(() => {
		if (selectedCat) {
			axios
				.get(`https://dummyjson.com/products/category/${selectedCat}`)
				.then((element) => {
					setFilterItems(element.data.products);
				});
		} else {
			setFilterItems(product); // عرض الكل
		}
	}, [selectedCat, product]);

	return (
		<div style={{
			height: "100%",
			margin
		}}>
			<Navbar />
			<div className="container-fluid"
			>
				<div className="row">
					<div className="col-md-2">
						<div className="sidebar">
							<button
								onClick={() => {
									setSelectedCat("");
								}}
								className="side-btns"
								style={{height:"70px"}}
							>
								Show All Categories
							</button>
							{cats.map((cat) => {
								return (
									<div key={cat}>
									<button
										className="side-btns"
										onClick={() => {
											setSelectedCat(cat);
										}}>
										{cat}
									</button>

									</div>
								);
							})}
						</div>
					</div>
					<div className="col-md-10 shop">
						<div className="container-fluid">
							<div className="row">
								{filterItems.map((item) => {
									return (
										<div key={item.id} className="col-md-4">
											<div className="item-card">
												<img src={item.images[0]} />
												<h3>{item.title}</h3>
												<h4>{item.price} $</h4>
												<h5>Rating: {item.rating}</h5>
												<h6>{item.category}</h6>
												<button
													className="side-btns"
													style={{
														height: "44px",
													}}>
													Add To Cart
												</button>
											</div>
										</div>
									);
								})}
							</div>
						</div>
					</div>
				</div>
			</div>
			<Footer />
		</div>
	);
}
export default AllShop;
